/**
 * Кастом хук для получения отправки 'token'
 * применяем для переиспользования и сокращении записи при работе с 'token'
 * @param {key} string. 'token'
 * @param {initialValue} string. или сгенирированный токен или пустая строка
 * localStorage.getItem(key) - получение значения по ключу
 */
import {useState, useEffect} from 'react';

export default (key, initialValue = '') => {
   const [value, setValue] = useState(() => {
      return localStorage.getItem(key) || initialValue
   })
   useEffect(() => {
      localStorage.setItem(key, value)
   }, [value, key])
   return [value, setValue]
}