import React, {useEffect, Fragment} from 'react';
import useFetch from 'hooks/useFetch';
import {stringify} from 'query-string';

import {Feed} from 'components/feet';
import {Pagination} from 'components/pagination.js';
import {PopularTags} from 'components/popularTags';
import {Loading} from 'components/loading';
import {ErrorMessage} from 'components/error';
import {FeedToggler} from 'components/feedToggler';
import {getPaginator, limit} from 'utils';
/**
 * 
 */
const GlobalFeed = ({location, match}) => {
    const url = match.url
    const {offset, currentPage} = getPaginator(location.search)
    // console.log("GlobalFeed",offset, currentPage)
    const stringefiedParams = stringify({
        limit,
        offset
    })
    const apiUrl = `/articles?${stringefiedParams}`
    const [{response, isLoading, error}, doFetch] = useFetch(apiUrl)
    // console.log('globalFeed: response:', response)
    // console.log('globalFeed: props:', props)

    useEffect(() => {
        doFetch()
    }, [doFetch, currentPage])

    return (
        <div className='home-page'>
            <div className='banner'>
                <div className='container'>
                   <h1>Medium clone</h1>
                   <p>A place to share knowleage</p>
                </div>
            </div>
            <div className='container page'>
                <div className='row'>
                    <div className='col-md-9'>
                        <FeedToggler/>
                        {isLoading && <Loading/>}
                        {error && <ErrorMessage/>}
                        {!isLoading && response && (
                            <Fragment>
                                <Feed articles={response.articles} />
                                <Pagination total={response.articlesCount} limit={limit} url={url} currentPage={currentPage} />
                            </Fragment>
                        )}
                    </div>
                    <div className='col-md-3'>
                        <PopularTags />
                    </div>
                </div>
            </div>
        </div>
    )
}
export default GlobalFeed;